"use client";
import { useEffect, useState } from "react";
import { useRouter } from "next/navigation";
import {
  GoogleAuthProvider,
  signInWithPopup,
  signInWithRedirect,
  getRedirectResult,
  onAuthStateChanged,
} from "firebase/auth";
import { auth, db } from "@/lib/firebase";
import { ADMIN_EMAILS } from "@/lib/config";
import {
  doc,
  getDoc,
  setDoc,
  updateDoc,
  deleteDoc,
  serverTimestamp,
} from "firebase/firestore";

export default function LoginPage() {
  const router = useRouter();
  const [currentImageIndex, setCurrentImageIndex] = useState(0);
  const [isAutoPlaying, setIsAutoPlaying] = useState(true);
  const [isLoading, setIsLoading] = useState(false);
  const [buttonClick, setButtonClick] = useState(false);
  
  // Bright, vibrant background images for carousel
  const backgroundImages = [
    "data:image/jpeg;base64,",
    "data:image/jpeg;base64,",
    "data:image/jpeg;base64,",
    "data:image/jpeg;base64,",
    "data:image/jpeg;base64,"
  ];

  // const backgroundImages = [
  //   "https://images.unsplash.com/photo-1554080351-a76ca804d5f0?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1770&q=80",
  //   "https://images.unsplash.com/photo-1649972904349-6e44c42644a7?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1770&q=80",
  //   "https://images.unsplash.com/photo-1488590528505-98d2b5aba04b?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1770&q=80"
  // ];



  // Auto-advance carousel
  useEffect(() => {
    let interval;
    if (isAutoPlaying) {
      interval = setInterval(() => {
        setCurrentImageIndex((prevIndex) => 
          prevIndex === backgroundImages.length - 1 ? 0 : prevIndex + 1
        );
      }, 5000);
    }
    return () => clearInterval(interval);
  }, [isAutoPlaying, backgroundImages.length]);
  
  // Handle manual navigation
  const goToSlide = (index) => {
    setCurrentImageIndex(index);
    setIsAutoPlaying(false);
    setTimeout(() => setIsAutoPlaying(true), 10000);
  };
  
  const goToPrevSlide = () => {
    setCurrentImageIndex((prevIndex) => 
      prevIndex === 0 ? backgroundImages.length - 1 : prevIndex - 1
    );
    setIsAutoPlaying(false);
    setTimeout(() => setIsAutoPlaying(true), 10000);
  };
  
  const goToNextSlide = () => {
    setCurrentImageIndex((prevIndex) => 
      prevIndex === backgroundImages.length - 1 ? 0 : prevIndex + 1
    );
    setIsAutoPlaying(false);
    setTimeout(() => setIsAutoPlaying(true), 10000);
  };
  
  // Fetch Location from IP
  const fetchLocation = async () => {
    try {
      const res = await fetch("https://ipapi.co/json/");
      return await res.json();
    } catch (e) {
      console.warn("Location fetch failed:", e);
      return null;
    }
  };
  
  // Firestore helper: create or update /users/{uid}
  const upsertUser = async (user) => {
    if (!user) return;
    const ref = doc(db, "users", user.uid);
    const snap = await getDoc(ref);
    // fetch location
    const loc = await fetchLocation();
    const base = {
      email: user.email ?? "",
      name: user.displayName ?? "",
      photoURL: user.photoURL ?? "",
      provider: user.providerData?.[0]?.providerId ?? "google",
      updatedAt: serverTimestamp(),
      lastLoginAt: serverTimestamp(),
      isOnline: true,
      location: loc ? `${loc.city}, ${loc.country_name}` : "Unknown",
    };
    if (!snap.exists()) {
      await setDoc(ref, { ...base, createdAt: serverTimestamp() });
    } else {
      await updateDoc(ref, base);
    }
    // create/update onlineUsers/{uid} doc
    await setDoc(doc(db, "onlineUsers", user.uid), {
      email: user.email,
      lastActive: serverTimestamp(),
    });
  };
  
  // Mark user offline on unload
  const markOfflineOnUnload = (uid) => {
    const handleUnload = async () => {
      try {
        await updateDoc(doc(db, "users", uid), { isOnline: false });
        await deleteDoc(doc(db, "onlineUsers", uid));
      } catch (err) {
        console.error("Error marking offline:", err);
      }
    };
    window.addEventListener("beforeunload", handleUnload);
    return () => window.removeEventListener("beforeunload", handleUnload);
  };
  
  // after login: save user -> route to /admin or /user
  const handleRedirectAfterLogin = async (user) => {
    if (!user) return;
    await upsertUser(user);
    // start offline cleanup
    markOfflineOnUnload(user.uid);
    if (ADMIN_EMAILS.includes(user.email)) {
      router.push("/admin");
    } else {
      router.push("/user");
    }
  };
  
  const handleGoogleLogin = async () => {
    // Set button click effect
    setButtonClick(true);
    setTimeout(() => setButtonClick(false), 300);
    
    // Set loading state
    setIsLoading(true);
    
    try {
      const provider = new GoogleAuthProvider();
      if (/Mobi|Android/i.test(navigator.userAgent)) {
        await signInWithRedirect(auth, provider);
      } else {
        const result = await signInWithPopup(auth, provider);
        await handleRedirectAfterLogin(result.user);
      }
    } catch (err) {
      console.error("Login error:", err);
      alert(err.message);
    } finally {
      // Only reset loading if not on mobile (since mobile uses redirect)
      if (!/Mobi|Android/i.test(navigator.userAgent)) {
        setIsLoading(false);
      }
    }
  };
  
  useEffect(() => {
    const unsub = onAuthStateChanged(auth, (user) => {
      if (user) {
        handleRedirectAfterLogin(user);
      }
    });
    getRedirectResult(auth)
      .then((result) => {
        if (result?.user) {
          handleRedirectAfterLogin(result.user);
        }
      })
      .catch((err) => console.error("Redirect login error:", err));
    return () => unsub();
  }, [router]);
  
  return (
    <div className="min-h-screen bg-black text-white flex flex-col items-center justify-center p-4 relative overflow-hidden">
      {/* Background Image Carousel - Mobile Optimized */}
      <div className="absolute inset-0 overflow-hidden">
        {backgroundImages.map((image, index) => (
          <div
            key={index}
            className={`absolute inset-0 transition-opacity duration-1000 ease-in-out ${
              currentImageIndex === index ? 'opacity-100' : 'opacity-0'
            }`}
          >
            <img
              src={image}
              alt={`Background ${index + 1}`}
              className="w-full h-full object-cover"
            />
            {/* Lighter gradient overlay for better image visibility */}
            <div className="absolute inset-0 bg-gradient-to-b from-black/40 via-black/30 to-black/60"></div>
          </div>
        ))}
      </div>
      
      {/* Enhanced Carousel Navigation Controls - Mobile Optimized */}
      <div className="absolute bottom-4 sm:bottom-8 left-0 right-0 z-20 flex justify-center space-x-2 sm:space-x-3 px-4">
        {backgroundImages.map((_, index) => (
          <button
            key={index}
            onClick={() => goToSlide(index)}
            className={`w-3 h-3 sm:w-4 sm:h-4 rounded-full transition-all duration-300 ${
              currentImageIndex === index
                ? 'bg-white scale-125 shadow-lg'
                : 'bg-white/70 hover:bg-white/90'
            }`}
            aria-label={`Go to slide ${index + 1}`}
          />
        ))}
      </div>
      
      {/* Enhanced Carousel Arrows - Mobile Optimized */}
      <button
        onClick={goToPrevSlide}
        className="absolute left-2 sm:left-4 top-1/2 transform -translate-y-1/2 z-20 bg-white/20 hover:bg-white/30 backdrop-blur-sm rounded-full p-2 sm:p-3 transition-all duration-300 shadow-lg"
        aria-label="Previous slide"
      >
        <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 sm:h-6 sm:w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 19l-7-7 7-7" />
        </svg>
      </button>
      
      <button
        onClick={goToNextSlide}
        className="absolute right-2 sm:right-4 top-1/2 transform -translate-y-1/2 z-20 bg-white/20 hover:bg-white/30 backdrop-blur-sm rounded-full p-2 sm:p-3 transition-all duration-300 shadow-lg"
        aria-label="Next slide"
      >
        <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 sm:h-6 sm:w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5l7 7-7 7" />
        </svg>
      </button>
      
      {/* Enhanced Login Card - Mobile Optimized */}
      <div className="relative z-10 w-full max-w-md sm:max-w-md transform transition-all duration-700 animate-fadeIn px-2">
        <div className="bg-white/90 backdrop-blur-lg rounded-2xl p-6 sm:p-8 border border-white/20 shadow-2xl">
          <div className="text-center mb-6 sm:mb-8">
            <div className="inline-flex items-center justify-center w-16 h-16 sm:w-20 sm:h-20 rounded-full bg-gradient-to-r from-blue-600 to-purple-600 mb-4 sm:mb-6 shadow-lg">
              <svg xmlns="http://www.w3.org/2000/svg" className="h-8 w-8 sm:h-10 sm:w-10 text-white" viewBox="0 0 20 20" fill="currentColor">
                <path fillRule="evenodd" d="M12.395 2.553a1 1 0 00-1.45-.385c-.345.23-.614.558-.822.88-.214.33-.403.713-.57 1.116-.334.804-.614 1.768-.84 2.734a31.365 31.365 0 00-.613 3.58 2.64 2.64 0 01-.945-1.067c-.328-.68-.398-1.534-.398-2.654A1 1 0 005.05 6.05 6.981 6.981 0 003 11a7 7 0 1011.95-4.95c-.592-.591-.98-.985-1.348-1.467-.363-.476-.724-1.063-1.207-2.03zM12.12 15.12A3 3 0 017 13s.879.5 2.5.5c0-1 .5-4 1.25-4.5.5 1 .786 1.293 1.371 1.879A2.99 2.99 0 0113 13a2.99 2.99 0 01-.879 2.121z" clipRule="evenodd" />
              </svg>
            </div>
            <h1 className="text-2xl sm:text-3xl md:text-4xl font-bold mb-2 text-gray-800">
              BrainFuel
            </h1>
            <p className="text-gray-600 text-sm sm:text-base">Sign in to access your dashboard</p>
          </div>
          
          {/* Enhanced Google Button with visual feedback - Mobile Optimized */}
          <button
            onClick={handleGoogleLogin}
            disabled={isLoading}
            className={`flex items-center justify-center gap-3 w-full py-3 sm:py-4 px-6 bg-gradient-to-r from-blue-600 to-purple-600 text-white font-medium rounded-xl shadow-lg hover:shadow-xl transform transition-all duration-300 ${
              buttonClick ? 'scale-95' : 'hover:scale-[1.02]'
            } ${isLoading ? 'opacity-80 cursor-not-allowed' : ''}`}
          >
            {isLoading ? (
              <>
                <svg className="animate-spin -ml-1 mr-2 h-5 w-5 text-white" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                  <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                  <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                </svg>
                <span>Signing in...</span>
              </>
            ) : (
              <>
                <img src="https://www.svgrepo.com/show/475656/google-color.svg" alt="Google" className="w-5 h-5 sm:w-6 sm:h-6" />
                <span className="font-medium">Continue with Google</span>
              </>
            )}
          </button>
          
          {/* Loading Overlay - Mobile Optimized */}
          {isLoading && (
            <div className="fixed inset-0 bg-black/50 flex items-center justify-center z-50 p-4">
              <div className="bg-white rounded-xl p-6 sm:p-8 flex flex-col items-center max-w-xs sm:max-w-sm mx-auto">
                <div className="relative">
                  <div className="w-12 h-12 sm:w-16 sm:h-16 border-4 border-blue-200 rounded-full"></div>
                  <div className="w-12 h-12 sm:w-16 sm:h-16 border-4 border-blue-600 border-t-transparent rounded-full animate-spin absolute top-0 left-0"></div>
                </div>
                <p className="mt-4 text-gray-700 font-medium text-sm sm:text-base">Authenticating with Google...</p>
                <p className="text-gray-500 text-xs sm:text-sm mt-2 text-center">Please wait while we sign you in securely</p>
              </div>
            </div>
          )}
          
          <div className="mt-6 sm:mt-8 text-center text-xs sm:text-sm text-gray-500">
            By signing in, you agree to our Terms of Service and Privacy Policy
          </div>
          
          {/* Enhanced Mobile Warning - Mobile Optimized */}
          <div className="bg-gradient-to-r from-amber-500 to-orange-500 text-white px-3 py-2 sm:px-4 sm:py-3 rounded-lg font-bold shadow-md animate-pulse block mt-4 sm:mt-6 text-xs sm:text-sm">
            ⚠️ Mobile user: Please enable "Desktop mode" in your browser before using this website. Very important!
          </div>
        </div>
      </div>
      
      <style jsx global>{`
        @keyframes fadeIn {
          from { opacity: 0; transform: translateY(20px); }
          to { opacity: 1; transform: translateY(0); }
        }
        .animate-fadeIn {
          animation: fadeIn 0.8s ease-out forwards;
        }
        
        /* Responsive adjustments */
        @media (max-width: 640px) {
          /* Adjust image carousel for mobile */
          .min-h-screen {
            padding-bottom: 60px; /* Ensure content isn't hidden by nav dots */
          }
        }
      `}</style>
    </div>
  );
}





